import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Tweet() {
  return (
    <div className='tweet'>
      <Avatar />
      <div className='content'>
        <NameWithHandle />
        <Message />
      </div>
      Tweet
    </div>
  );
}

function Avatar() {
  return (
    <img
      src='https://www.gravatar.com/avatar/8f0896de99c04b103e1f29cbf34b580a'
      className='avatar'
      alt='avatar'
    />
  );
}

function Message() {
  return (
    <div className='message'>
      This is less than 120 characters
    </div>
  );
}

function NameWithHandle() {
  return (
    <span className='name-with-handle'>
      <span classNAme='name'>John Espinosa</span>
      <span className='handle'>@Jespn88</span>
    </span>
  );
}

ReactDOM.render(<Tweet />, document.getElementById('root'));
