import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Tweet() {
  return (
    <div className='tweet'>
      <Avatar/>
      <div className='content'>
        <NameWithHandle/>
        <Message/>
      </div>
    </div>
  );
}

function Avatar() {
  return (
    <img
      src='https://www.gravatar.com/avatar/8f0896de99c04b103e1f29cbf34b580a'
      className='avatar'
      alt='avatar'
    />
  );
}

function Message() {
  return (
    <div className='message'>
      This is less than 120 characters
    </div>
  );
}

function NameWithHandle() {
  return (
    <span className='name-with-handle'>
      <span className='name'>John Espinosa</span>
      <span className='handle'>@Jespn88</span>
    </span>
  );
}

const Time = () => ( <span className="time">3h ago</span> );

const ReplyButton = () => ( <i className="fa fa-reply reply-button"/> );

const RetweetButton = () => ( <i className="fa fa-retweet retweet-button"/> );

const LikeButton = () => ( <i className="fa fa-heart like-button"/> );

const MoreOptionsButton = () => ( <i className="fa fa-ellipsis-h more-options-button"/> );

ReactDOM.render(<Tweet />, document.getElementById('root'));
