import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Tweet({ tweet }) {
  return (
    <div className='tweet'>
      <Avatar hash={tweet.gravatar}/>
      <div className='content'>
        <NameWithHandle/><Time/>
        <Message/>
        <div className='buttons'>
          <ReplyButton/>
          <RetweetButton/>
          <LikeButton/>
          <MoreOptionsButton/>
        </div>
      </div>
    </div>
  );
}

function Avatar({ hash}) {
  let url = `https://www.gravatar.com/avatar/${hash}`
  return (
    <img
      src={url}
      className='avatar'
      alt='avatar'
    />
  );
}

function Message() {
  return (
    <div className='message'>
      This is less than 120 characters
    </div>
  );
}

function NameWithHandle() {
  return (
    <span className='name-with-handle'>
      <span className='name'>John Espinosa</span>
      <span className='handle'>@Jespn88</span>
    </span>
  );
}

const Time = () => ( <span className="time">3h ago</span> );

const ReplyButton = () => ( <i className="fa fa-reply reply-button"/> );

const RetweetButton = () => ( <i className="fa fa-retweet retweet-button"/> );

const LikeButton = () => ( <i className="fa fa-heart like-button"/> );

const MoreOptionsButton = () => ( <i className="fa fa-ellipsis-h more-options-button"/> );

var testTweet = {
  message: "Something about cats.", gravatar: "8f0896de99c04b103e1f29cbf34b580a",
  author: {
  handle: "catperson",
  name: "IAMA Cat Person" },
  likes: 2,
  retweets: 0,
  timestamp: "2016-07-30 21:24:37"
};

ReactDOM.render(
  <Tweet tweet={testTweet}/>, 
  document.querySelector('#root')
);
